import {useState} from 'react';

import {Nav_admin} from './Nav_admin';

export const Update_staff = (props) => {

    const [fname,setFname] = useState('');
    const [lname,setLname] = useState('');
    const [address,setAddress] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        const post = {fname,lname,address,password,role};
        console.log("this is post");
        console.log(post);

        var params = 'fname=' +fname+ '&lname=' +lname+  '&address=' +address+ '&password=' +password+ '&role=' +role+ '&db=' + "Staff";



        fetch('http://localhost:8000/addstaff',{
            method:'POST',
            headers: {"Content-Type": 'application/x-www-form-urlencoded'},
            body: params
        }).then(() =>{
          props.set(0);
          props.set(1);
          alert("Posted Succesfuly");

        })
    }



return (

<div>

    <Nav_admin/>


<p>
    Update customer information:
  </p><br></br>


   <form onSubmit={handleSubmitfname}>
    <label for="fname">First Name:</label> &nbsp;&nbsp;
       <input type="text" id="fname" name="First Name"></input>  <button  onclick= "updateFname()"> update  </button><br></br><br></br>
       </form>
       <form onSubmit={handleSubmitlname}>
    <label for="lname">Last Name:</label>  &nbsp;&nbsp;  
       <input type="text" id="lname" name="Last Name"></input> <button  onclick= "updateLname()"> update  </button><br></br><br></br>
       </form>
    <form onSubmit={handleSubmitAddress}>
    <label for="address">Address:</label>   &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
       <input type="text" id="address" name="Address"></input> <button  onclick= "updateAddress()"> update  </button><br></br><br></br><br></br><br></br><br></br><br></br>
       </form>




</div> 


);


}
