import './App.css';



import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import {useState} from 'react';
import React, { useEffect } from "react";

import {Landing} from './components/Landing';
import {ShowPosts} from './components/ShowPosts';
import {AddPosts} from './components/AddPosts';
import {Post} from "./components/Post"

function App() {  

  var change = 0;
  const [getList, setList] = useState([]);
  useEffect(() => {   {fetch('http://localhost:8000/data').then
  (response => response.json()).then
  (response => setList(response))}; },[change]);

  return (
    <div className="App">
      <header className="App-header">

        <p>
        <Router>
        <Link to="/showPosts">  <button> Show Posts </button> </Link>
        <Link to="/addPosts">  <button> Add Posts </button>   </Link>
         <Routes>

         <Route exact path='/' element={<Landing/>} />
         <Route path="/showPosts" element={<ShowPosts get = {getList}/>} />
        <Route path="/addPosts" element={ <AddPosts set = {setList} /> } />


           </Routes>

        </Router>
        </p>

      </header>

      </div>
  );
}

export default App;
